import "./TodoItem.css";

function TodoItem(props: any) {

  function changeStatus() {
    let copy = []
    copy[props.id].isDone = !(props.isDone)
    props.setTASKS(copy)
  }

  return (
    <div className="TodoItem">
      <div>
        <input
          type="checkbox"
          checked={props.isDone}
          onChange={() => changeStatus()}
        ></input>
        <label>{props.task}</label>
      </div>

      <div className="icons">
        <h4>delete|</h4>
        <h4>edit</h4>
      </div>
    </div>
  );
}

export default TodoItem;
