import React from "react";
import "./TodoItem.css";
import { ComponentInterface, TaskInterface } from "../../App";

function TodoItem(props: ComponentInterface) {
  function changeStatus(event: React.ChangeEvent<HTMLInputElement>) {
    let copyOfTasks = props.TASKS
    copyOfTasks[props.Task.id].status = event.target.checked
  }

  return (
    <div className="TodoItem">
      <div>
        <input
          type="checkbox"
          checked={props.Task.status}
          onChange={(event) => changeStatus}
        ></input>
        <label>{props.Task.task}</label>
      </div>

      <div className="icons">
        <h4>delete|</h4>
        <h4>edit</h4>
      </div>
    </div>
  );
}

export default TodoItem;
