import React, { useState, useReducer, useRef, HtmlHTMLAttributes } from "react";
import "./App.css";
import TodoItem from "./components/toDoITem/TodoItem";

interface TaskInterface {
  id: number;
  task: string;
  isDone: boolean;
}

interface actionType {
  type: string;
  payload: number;
  task:string;
}

const TASKS = [
  {
    id: 0,
    task: "TASK1",
    isDone: false,
  },
  {
    id: 1,
    task: "TASK2",
    isDone: true,
  },
];

type state = typeof TASKS;

function reducer(state: state, action: actionType) {
  const copy = [...state];
  switch (action.type) {
    case "CHANGE_STATUS":
      copy[action.id].isDone = !copy[action.id].isDone;
      return copy;
    default:
      throw new Error();
  }
}

function App() {
  const [value, setValue] = useState('')
  const [showTasks, setShowTasks] = useState("All");
  const inputRef = useRef<HTMLInputElement> (null)
  function filter(task: TaskInterface) {
    if (showTasks === "Completed") {
      if (task.isDone) {
        return (
          <TodoItem
            TASKS={TASKS}
            dispatch={dispatch}
            id={task.id}
            task={task.task}
            isDone={task.isDone}
          />
        );
      }
    } else if (showTasks === "Incomplete") {
      if (!task.isDone) {
        return (
          <TodoItem
            TASKS={TASKS}
            dispatch={dispatch}
            id={task.id}
            task={task.task}
            isDone={task.isDone}
          />
        );
      }
    }
  }



  const [state, dispatch] = useReducer(reducer, TASKS);
  return (
    <div className="App">
      <header className="header">TODO LIST</header>
      <div className="divDef">
        <select
          value={showTasks}
          onChange={(event) => {
            setShowTasks(event.target.value);
          }}
        >
          <option>All</option>
          <option>Completed</option>
          <option>Incomplete</option>
        </select>
      </div>
      <div className="taskContainer">
        {state.map((task) => {
          if (showTasks !== "All") {
            return filter(task);
          }

          return (
            <TodoItem
              TASKS={TASKS}
              dispatch={dispatch}
              id={task.id}
              task={task.task}
              isDone={task.isDone}
            />
          );
        })}
      </div>
      <div className="inputContainer">
        <input className="input" ref={inputRef} value={value} onChange={(event) => setValue(event.target.value)}></input>
        <button className="addBtn" onClick={() => {
          inputRef.current && setValue(inputRef.current.value)
        }}>Add Task</button>
      </div>
    </div>
  );
}

export default App;
