import React, { useState, useReducer } from "react";
import "./App.css";
import TodoItem from "./components/toDoITem/TodoItem";

export interface TaskInterface {
  id: number;
  task: string;
  status: boolean;
}

export interface ComponentInterface {
  TASKS: TaskInterface[];
  setTasks: any;
  Task: TaskInterface;
}



const TASKS = [
  {
    id: 0,
    task: "Task1",
    status: true,
  },
  {
    id: 1,
    task: "Task2",
    status: false,
  },
  {
    id: 2,
    task: "Task3",
    status: true,
  },
];


