import React from "react";
import "./TodoItem.css";
import { ComponentInterface, TaskInterface } from "../../App";

function TodoItem(props: ComponentInterface) {
  function changeStatus(event: React.ChangeEvent<HTMLInputElement>) {
    let copyOfTasks = props.TASKS
    copyOfTasks[props.Task.id].status = !event.
    console.log(copyOfTasks)
    props.setTasks(copyOfTasks);
  }


  return (
    <div className="TodoItem">
      <div>
        <input
          type="checkbox"
          checked={props.TASKS[props.Task.id].status}
          onChange={(event) => {changeStatus(event)}}
        ></input>
        <label>{props.TASKS[props.Task.id].task}</label>
      </div>

      <div className="icons">
        <h4>delete|</h4>
        <h4>edit</h4>
      </div>
    </div>
  );
}

export default TodoItem;
