import React, { useState, useReducer } from "react";
import "./App.css";
import TodoItem from "./components/toDoITem/TodoItem";

interface TaskInterface {
  id: number;
  task: string;
  isDone: boolean;
}

interface actionType {
  type: string;
  id: number;
}

const TASKS = [
  {
    id: 0,
    task: "TASK1",
    isDone: false,
  },
  {
    id: 1,
    task: "TASK2",
    isDone: true,
  },
];

type state = typeof TASKS;

function reducer(state: state, action: actionType) {
  const copy = { ...state };
  switch (action.type) {
    case "CHANGE_STATUS":
      console.log("hello");
      copy[action.id].isDone = !(copy[action.id].isDone);
      return copy;
    default:
      throw new Error();
  }
}

function App() {
  const [showTasks, setShowTasks] = useState("All");
  const [state, dispatch] = useReducer(reducer, TASKS);
  return (
    <div className="App">
      <header className="header">TODO LIST</header>
      <div className="divDef">
        <select
          value={showTasks}
          onChange={(event) => {
            setShowTasks(event.target.value);
          }}
        >
          <option>All</option>
          <option>Completed</option>
          <option>Incomplete</option>
        </select>
      </div>
      <div className="taskContainer">
        {state.map((task) => (
              <div className="TodoItem">
              <div>
                <input
                  type="checkbox"
                  checked={task.isDone}
                  onChange={() => dispatch({ id: task.id, type: "CHANGE_STATUS" })}
                ></input>
                <label>{task.task}</label>
              </div>

              <div className="icons">
                <h4>delete|</h4>
                <h4>edit</h4>
              </div>
            </div>
        ))}
      </div>
      <div className="divDef">
        <button className="addBtn">Add Task</button>
      </div>
    </div>
  );
}

export default App;
