import React, { useState, useReducer } from "react";
import "./App.css";
import TodoItem from "./components/toDoITem/TodoItem";

interface TaskInterface {
  id: number;
  task: string;
  isDone: boolean;
}

interface actionType {
  type:string;
  id:number,

}

const TASKS: TaskInterface[] = [
  {
    id: 0,
    task: "TASK1",
    isDone: false,
  },
  {
    id: 1,
    task: "TASK2",
    isDone: true,
  },
];

function reducer(state:any , action:any) {
  const copy:TaskInterface[] = state
  switch (action.type) {
    case "CHANGE_STATUS":
      copy[0].isDone = !copy[0].isDone 
      return  {copy}
    default:
      throw new Error();
  }
}

function App() {
  const [showTasks, setShowTasks] = useState("All");
  const [state, dispatch] = useReducer(reducer, TASKS);
  return (
    <div className="App">
      <header className="header">TODO LIST</header>
      <div className="divDef">
        <select
          value={showTasks}
          onChange={(event) => {
            setShowTasks(event.target.value);
          }}
        >
          <option>All</option>
          <option>Completed</option>
          <option>Incomplete</option>
        </select>
      </div>
      <div className="taskContainer">
        {state.map((task:TaskInterface)=> (
          <TodoItem TASKS={state} task={task.task} id={task.id} isDone={task.isDone}/>
        ))}
      </div>
      <div className="divDef">
        <button className="addBtn">Add Task</button>
      </div>
    </div>
  );
}

export default App;
